package assignments_folder;

public class Assignment38 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int fact = 1;
		int n = 5;
		for (int i = 1; i <= n; i++) {
			fact = fact * i;
		}
		System.out.println("Factorial of number" + " " + n + " " + "is:" + fact);

	}

}
